await System.Console.Out.WriteLineAsync(((IEnumerable<int>)[int.Parse((await System.Console.In.ReadLineAsync())!)]).Select(intN => ((int[][])[Enumerable.Range(2, intN * 2).ToArray()]).Select(async it =>{ await System.Console.Out.WriteLineAsync(it[^1].ToString()); return it.Where(item => !it.Any(arrItem =>item%arrItem == 0 && item != arrItem));}).Select(item=>item.GetAwaiter().GetResult()).First().OrderBy(item => Math.Abs(item - intN)).FirstOrDefault(1)).First().ToString());
